"use strict";
var nodemailer = require("nodemailer");
var Common = (function () {
    function Common() {
    }
    Common.sendMailWithAttachment = function (to, from, subject, message, html, attachmentfilename, attachmentfilepath, callback) {
        console.log('sending mail');
        console.log('Common.SEND_FROM_EMAIL : ', Common.SEND_FROM_EMAIL);
        var mailOptions = {
            to: to,
            from: Common.SEND_FROM_EMAIL,
            subject: subject,
            text: message,
            html: html,
            attachments: [
                {
                    filename: attachmentfilename,
                    path: attachmentfilepath
                }
            ]
        };
        var smtpTransport = nodemailer.createTransport(Common.MAIL_CONFIG);
        smtpTransport.sendMail(mailOptions, callback);
    };
    Common.sendMail = function (to, from, subject, message, html, callback) {
        console.log('sending application for subject ', subject);
        console.log('Common.SEND_FROM_EMAIL : ', Common.SEND_FROM_EMAIL);
        var mailOptions = {
            to: to,
            from: Common.SEND_FROM_EMAIL,
            subject: subject,
            text: message,
            html: html,
        };
        var smtpTransport = nodemailer.createTransport(Common.MAIL_CONFIG);
        var mailsent = smtpTransport.sendMail(mailOptions, callback);
        console.log('Check Mail Sent: ', mailsent);
        if (mailsent) {
            console.log('mailsent: ', mailsent);
        }
    };
    return Common;
}());
